import React from 'react';
import styled from 'styled-components';
import BgImg from '../../../..//images/Capture.PNG';


const Section = styled.section`
  background-image: url(${BgImg});
  height: 750px;
  display: block;
  background-repeat: no-repeat;
  background-size: contain;
`;

const Content = styled.div`
  width: 100%;
  height: 100px;
`;

const Left = styled.div`

  padding-top: 60px;
`;

const Title = styled.p`
    text-align: center;
  font-size: 55px;
  color: #04050a;
  font-weight: 400;
`;

const Desc = styled.p`
  text-align: center;
  font-size: 18px;
  color: #9ea0ac;
  line-height: 30px;
  margin-top: 40px;
  text-align: center;
`;

const Button = styled.a`
  
  
`;

const Hero = () => {
  return (
    <Section>
      <Content>
        <Left>
          <Title>
          Butuh Informasi Terkait  <br />Internship Sesuai Dengan Bidang<br/>dan Kebutuhan Kamu 
          </Title>
          <Desc>
          Download Aplikasi GunMA dengan klik link dibawah ini.
          </Desc>
          <Button href='https://bit.ly/webull-join' target='_blank'>
            <span>Find It Now</span>
          
          </Button>
        </Left>
      </Content>
    </Section>
  );
};

export default Hero;
