import React from 'react';
import styled from 'styled-components';

const Section1 = styled.section`
  height: 750px;
  width: 1000px;
  display: block;
  margin : auto
  background-repeat: no-repeat;
  background-size: contain;
  box-shadow: 0 15px 14px rgb(0 42 177 / 12%);
  border-radius: 18px;
`;

const Content1 = styled.div`
  width: 100%;
  height: 100px;
`;

const Left = styled.div`
  padding-top: 110px;
`;

const Title = styled.p`
  font-size: 55px;
  color: #04050a;
  font-weight: 400;
`;

const Desc = styled.p`
padding-left: 30rem;
  width: 900px;
  font-size: 18px;
  color: #9ea0ac;
  line-height: 30px;
  margin-top: 40px;
`;

const Button = styled.a`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 18px;
  margin-top: 30px;
  width: 150px;
  height: 51px;
  line-height: 71px;
  font-size: 20px;
  text-align: center;
  color: #000000;
  cursor: pointer;
  background: #FAE57C;
  text-decoration: none;
  box-shadow: 0 15px 14px rgb(0 42 177 / 12%);
`;

const AboutUs = () => {
  return (
    <Section1>
      <Content1>
        <Left>
          <Title>
         Vision  <br /> 
          </Title>
          <Desc>
          Make it easier for someone to get an internship according to what they want
          </Desc>
          <Title>
          Mission  <br /> 
          </Title>
          <Desc>
          Providing services related to internships to provide opportunities for the younger generation to hone their skills.<br/>Accommodating internship information and providing opportunity for each achiever to gain experience in terms of hard skills and soft skills
          </Desc>
        </Left>
      </Content1>
    </Section1>
  );
};

export default AboutUs;
