import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Popup from 'reactjs-popup';
import Regis from './Regis';
import styled from 'styled-components';
import { useHistory } from 'react-router-dom';
import { Alert } from 'react-bootstrap';

const Section = styled.section`
  background-color: #EDEFFD;
  height: 350px;
  width: 500px;
  display: block;
  background-repeat: no-repeat;
  background-size: contain;
  align-items: center;
  border-radius: 15px;
  box-shadow: 0 10px 10px rgb(0 42 177 / 10%);
`;

const Title = styled.p`
text-align: center;
  color: #04050a;
  font-weight: 300;
  font-size: 35px;
`;

const Label = styled.p`
padding-left: 60px;
`;

const LabelPas = styled.p`
padding-left: 50px;
`;

const Left = styled.div`
  padding-left: 165px;
  padding-top: 25px;
`;

const LeftButton = styled.div`
  padding-left: 40px;
`;

const Right = styled.div`
  padding-top: 10px;
  padding-left: 465px;
`;

const Acc = styled.div`
padding-left: 8px;
font-size: 12px;
color : #326A88;
`;

const Button = styled.a`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 15px;
  margin-top: 40px;
  width: 90px;
  height: 35px;
  line-height: 50px;
  font-size: 17px;
  text-align: center;
  color: #000000;
  cursor: pointer;
  background: #FAE57C;
  text-decoration: none;
  box-shadow: 0 15px 14px rgb(0 42 177 / 12%);
`;


function Login({close}) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(false);
  const history = useHistory();
  
  //useEffect(() => {
  //  if (localStorage.getItem('user-info')){
  //    setError(false);
  //    //history.push('/');
  //  }
  //  else
  //  {
  //    setError(error);
  //  }
  //  return () => {};
  //}, [email, password])

  async function handleLogin () {
    console.warn(email, password)
    let item ={email, password};
    let result = await fetch('http://127.0.0.1:8000/api/login', {
        method : 'POST' ,
        headers : {
          "Accept": "application/json"
        },
        body: JSON.stringify(item)
    });
      result = await result.json();
      localStorage.setItem("user-info",JSON.stringify(result))
      history.push('/list');
  }

    return ( 
      <Alert>
      {error && <Alert align="center" variant="danger">Salah Bro!!!</Alert>}
      <Section>
        <div> {            
          <form>
            <Right>
          <a href className="close" onClick={close}>
          &times;
          </a>
          </Right>
          <Title>
          Login 
          </Title>
          <Left>
            <div className = "form-group" >
            <Label> <br/>Email : <br/> </Label> 
            <input type="email" 
            placeholder="Enter email" 
            onChange={(e) => setEmail(e.target.value)} />
            </div>
            <div className = "form-group" >
            <LabelPas ><br/> Password : <br/> </LabelPas> 
            <input type="password"
            placeholder="Enter password"  
            onChange={(e) => setPassword(e.target.value)} />            
            </div>
            <LeftButton>
            <div className="Login">
            <Button type = "submit"
            className = "btn btn-dark btn-lg btn-block"
            onClick={handleLogin}> Login </Button>
            </div>
            </LeftButton>
            <Acc>
            <a align="center" href>  Don't Have Account ?
            <Popup onClick={close} modal trigger={<u> Sign Up</u>}>
            {close => <Regis close={close} />}
            </Popup>
            </a> 
            </Acc>
            </Left>
            </form>
            }
            </div>
            </Section>
            </Alert>
        )
      }
export default Login;
