import React, { useEffect, useState } from "react";
import { Row, Col, Container } from "react-bootstrap";
import axios from "axios";
import { Link } from 'react-router-dom';
import CardItemList from './CardItemList';

export default function List() {

  const [data, setData] = useState([]);
  
  useEffect(() => {
    axios
        .get('https://ghibliapi.herokuapp.com/films')
        .then((res) => {
            console.log(res.data);
            setData(res.data);
            
        })
        .catch((err) => {
            console.log(err);
        }); 
}, []);

      return (
        <section className="section-card">
        <div className='cards'>

        { data.map(data=>
        <Container fluid>

      <ul className='cardList__items'>
            <CardItemList
              src='images/paid.png'
              text='One of the features that GunMA has is paid unpaid which will display companies that pay internally, so that users are expected to be able to choose the company according to their needs.'
              label={data.id}
              path='/'
            />
         
         
          </ul>
        
              <Row>


              <h4>
                    <strong>{}</strong>
                  </h4>
               
                <Col className="mt-3">
                  
                  <hr />
                  <Row className="overflow-auto menu">
   
                  </Row>
                </Col>
 
              </Row>
            </Container>
            )}
            
          </div>
          </section>

          
      );
      
    }
