import React, { useState } from 'react';
import Login from './Login';
import axios from 'axios';
import Popup from "reactjs-popup";
import styled from 'styled-components';
import { useHistory } from 'react-router';

const Section = styled.section`
  background-color: #EDEFFD;
  height: 400px;
  width: 500px;
  display: block;
  background-repeat: no-repeat;
  background-size: contain;
  align-items: center;
  border-radius: 15px;
  box-shadow: 0 10px 10px rgb(0 42 177 / 10%);
`;

const Title = styled.p`
text-align: center;
  color: #04050a;
  font-weight: 300;
  font-size: 35px;
`;

const Label = styled.p`
padding-left: 60px;
`;

const LabelEmail = styled.p`
padding-left: 60px;
`;

const LabelPas = styled.p`
padding-left: 50px;
`;

//const LabelRePas = styled.p`
//padding-left: 20px;
//`;

const Left = styled.div`
  padding-left: 165px;
  padding-top: 25px;
`;

const LeftButton = styled.div`
  padding-left: 40px;
`;

const Right = styled.div`
  padding-top: 10px;
  padding-left: 465px;
`;

const Acc = styled.div`
padding-left: 8px;
font-size: 12px;
color : #326A88;
`;

const Button = styled.a`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 15px;
  margin-top: 40px;
  width: 90px;
  height: 35px;
  line-height: 50px;
  font-size: 17px;
  text-align: center;
  color: #000000;
  cursor: pointer;
  background: #FAE57C;
  text-decoration: none;
  box-shadow: 0 15px 14px rgb(0 42 177 / 12%);
`;

function Regis({close}) {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [setFlag] = useState(false);
    const [login, setLogin] = useState(true);


    // on form submit...
    function handleFormSubmit(e) {
        e.preventDefault();

        if (!email || !password) {
            setFlag(true);

        } else {
            setFlag(false);
            localStorage.setItem("Email", JSON.stringify(email));
            localStorage.setItem("Password", JSON.stringify(password));
            console.log("Saved in Local Storage");

            setLogin(!login)
        }
    }


    return ( 
        <Section>
         {
            Login ? 
            <div> { login ? < form onSubmit = { handleFormSubmit } >
                <a className="close" href onClick={close}>
                &times;
                </a>
                <h3 > Register </h3> 
                <div className = "form-group" >
                <label > Mohon Diisi Semua </label> 
                </div> 
                <div className = "form-group" >
                <label > Email </label> 
                <input type = "email"
                className = "form-control"
                placeholder = "Enter email"
                onChange = {
                    (event) => setEmail(event.target.value) }
                /> 
                </div>
                <div className = "form-group" >
                <label > Password </label> 
                <input type = "password"
                className = "form-control"
                placeholder = "Enter password"
                onChange = {
                    (event) => setPassword(event.target.value) }
                /> 
                </div>
                <div>
                <button type = "submit"
                className = "btn btn-dark btn-lg btn-block" > Register </button> 
                </div>
                <div>
                <a>already Registered ?
                <Popup modal trigger={<u> Sign In</u>}>
                {close => <Regis close={close} />}
                </Popup>
                </a>
                </div>
                </form> : <Login />
            } 
            </div> : <div> 
            </div>
        } </Section>
    )
}

export default Regis
