import React from "react";
import { Col, Card } from "react-bootstrap";


const Menus = ({ menu }) => {
  return (
    <div className='cardList'>
       <Card.Img
          variant="top"
          src={
            "assets/images/" +
            menu.category.nama.toLowerCase() +
            "/" +
            menu.gambar
          }
        />
        <Card.Body>
          <Card.Title>{menu.nama} <strong>({menu.kode})</strong></Card.Title>
        </Card.Body>
    </div>
  );
};

export default Menus;
